import Dashboard from "./Pages/Dashboard";
import Login from "./Pages/Login";

function setToken(jwtToken){
  sessionStorage.setItem('token', JSON.stringify(jwtToken))
}

function getToken(){

}

function App() {

  const token = getToken();

  if(!token){
    return <Login setToken={token} />
  }

  return (
    <div className="App">
      <BrowserRouter>
        <Switch>
          <Route exact path="/">
            <Dashboard />
          </Route>
        </Switch>
      </BrowserRouter>
    </div>
  );
}

export default App;
